import React, {Component} from 'react';
import Header from "../header";

class AuthorsPage extends Component {

    render() {

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Authors</h1>

                        <div className="genres-tools">
                            <a className="btn btn-outline-danger float-right my-1 item-control" data-toggle="collapse" role="button" onClick={this.onDeleteButtonClick}>
                                <i className="fa fa-trash-o"/>
                            </a>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-authors-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-authors-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        {/*<div className="collapse" id="add-genre-block">*/}
                        {/*    <div className="card card-body mb-2">*/}
                        {/*        <form onSubmit={}>*/}
                        {/*            <div className="genre-form">*/}
                        {/*                <input type="text" placeholder="Write new genre here..." className="form-control" onChange={} value={}/>*/}
                        {/*                <button type="submit" className="btn btn-info auth-button">Add</button>*/}
                        {/*            </div>*/}
                        {/*        </form>*/}
                        {/*    </div>*/}
                        {/*</div>*/}

                        {/*<div className="collapse" id="edit-genre-block">*/}
                        {/*    <div className="card card-body">*/}
                        {/*        <form onSubmit={}>*/}
                        {/*            <input type="text" placeholder="Write new genre here..." className="form-control" onChange={} value={}/>*/}
                        {/*            <button type="submit" className="btn btn-info auth-button">Update</button>*/}
                        {/*        </form>*/}
                        {/*    </div>*/}
                        {/*</div>*/}

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Surname</th>
                                <th>Name</th>
                                <th>Patronymic</th>
                            </tr>
                            </thead>
                            <tbody>
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }
}

export default AuthorsPage;
