import React, {Component} from "react";
import "./libraries-page.css";
import Header from "../header";
import Spinner from "../spinner";
import axios from "axios";

class LibrariesPage extends Component {

    state = {
        libraries : [],
        loading : true,
        error : false,
        addLibrary : {
            addName : "",
            addAddress : "",
            addTelephone : ""
        },
        editLibrary : {
            editName : "",
            editAddress : "",
            editTelephone : ""
        },
        selectedItem : null
    };

    componentDidMount() {
        this.getAllLibraries();
    }

    getAllLibraries = () => {
        axios.get("http://localhost:8080/libraries")
            .then(response => {
                if(response.data != null) {
                    this.setState({
                        ...this.state,
                        libraries : response.data,
                        loading : false
                    })
                }
            })
    };

    onAddNameChange = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            addLibrary : {
                ...this.state.addLibrary,
                addName : event.target.value
            }
        })
    };

    onAddAddressChange = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            addLibrary : {
                ...this.state.addLibrary,
                addAddress : event.target.value
            }
        })
    };

    onAddTelephoneChange = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            addLibrary : {
                ...this.state.addLibrary,
                addTelephone : event.target.value
            }
        })
    };

    onAddLibrary = async event => {
        event.preventDefault();

        const library = {
            name : this.state.addLibrary.addName,
            address : this.state.addLibrary.addAddress,
            telephone : this.state.addLibrary.addTelephone
        };

        await axios.post("http://localhost:8080/library-add", library)
            .then(response => {
                if(response.data != null) {
                    this.setState({
                        ...this.state,
                        addLibrary : {
                            addName : "",
                            addAddress : "",
                            addTelephone : ""
                        }
                    })
                }
            });

        this.getAllLibraries();

    };

    selectedRow = (id, name, address, telephone) => {
        this.setState({
            ...this.state,
            editLibrary : {
                editName : name,
                editAddress: address,
                editTelephone: telephone
            },
            selectedItem : id
        })
    };

    render () {

        if(this.state.loading) {
            return <Spinner/>
        }

        const rows = this.state.libraries.map(({id, name, address, telephone})=>{
            return(
                <tr>
                    <td><input type="radio" name="object_id" value={id} onClick={() => {this.selectedRow(id, name, address, telephone)}}/></td>
                    <td>{name}</td>
                    <td>{address}</td>
                    <td>{telephone}</td>
                </tr>
            )
        });

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Libraries</h1>

                        <div className="genres-tools">
                            <a className="btn btn-outline-danger float-right my-1 item-control" data-toggle="collapse" role="button">
                                <i className="fa fa-trash-o"/>
                            </a>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-library-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-library-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-library-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onAddLibrary}>
                                    <div className="genre-form">
                                        <input type="text" placeholder="Write name of new library here..." className="form-control mb-2" onChange={this.onAddNameChange} value={this.state.addLibrary.addName}/>
                                        <input type="text" placeholder="Write address of new library here..." className="form-control mb-2" onChange={this.onAddAddressChange} value={this.state.addLibrary.addAddress}/>
                                        <input type="text" placeholder="Write telephone of new library here..." className="form-control mb-2" onChange={this.onAddTelephoneChange} value={this.state.addLibrary.addTelephone}/>
                                        <button type="submit" className="btn btn-info auth-button">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        {/*<div className="collapse" id="edit-genre-block">*/}
                        {/*    <div className="card card-body">*/}
                        {/*        <form onSubmit={this.onEditButtonClick}>*/}
                        {/*            <input type="text" placeholder="Write new genre here..." className="form-control" onChange={this.onEditGenreChange} value={this.state.editGenreField}/>*/}
                        {/*            <button type="submit" className="btn btn-info auth-button">Update</button>*/}
                        {/*        </form>*/}
                        {/*    </div>*/}
                        {/*</div>*/}

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Name</th>
                                <th>Address</th>
                                <th>Telephone</th>
                            </tr>
                            </thead>
                            <tbody>
                            {rows}
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }


}

export default LibrariesPage;
