import React, {Component} from 'react';
import Header from "../header";
import Spinner from "../spinner";
import {compose} from "../../utils";
import {connect} from "react-redux";
import withLibraryService from "../../hoc";
import {
    authorsRequested,
    authorsLoaded,
    authorUpdateAddSurnameField,
    authorUpdateAddNameField,
    authorUpdateAddPatronymicField,
    authorUpdateEditSurnameField,
    authorUpdateEditNameField,
    authorUpdateEditPatronymicField,
    authorUpdateSelectedItem
    } from "../../actions/authors-actions"

class AuthorsPage extends Component {

    componentDidMount() {
        this.props.onRequest();
        this.getAllAuthors();
    }

    getAllAuthors = async () => {
        const {libraryService} = this.props;
        const authors = await libraryService.getAllAuthors();
        this.props.onLoaded(authors);
    };

    changeAddSurname = event => {
        event.preventDefault();
        this.props.changeAddSurname(event.target.value);
    };

    changeAddName = event => {
        event.preventDefault();
        this.props.changeAddName(event.target.value);
    };

    changeAddPatronymic = event => {
        event.preventDefault();
        this.props.changeAddPatronymic(event.target.value);
    };

    changeEditSurname = event => {
        event.preventDefault();
        this.props.changeEditSurname(event.target.value);
    };

    changeEditName = event => {
        event.preventDefault();
        this.props.changeEditName(event.target.value);
    };

    changeEditPatronymic = event => {
        event.preventDefault();
        this.props.changeEditPatronymic(event.target.value);
    };

    onAddAuthor = async event => {
        event.preventDefault();

        const {addSurname, addName, addPatronymic} = this.props.addAuthor;

        const author = {
            surname : addSurname,
            name : addName,
            patronymic : addPatronymic
        };

        await this.props.libraryService.addAuthor(author);
        this.props.onRequest();
        this.getAllAuthors();
    };

    onEditAuthor = async event => {
        event.preventDefault();

        const {editSurname, editName, editPatronymic} = this.props.editAuthor;

        const author = {
            id : this.props.selectedItem,
            surname : editSurname,
            name : editName,
            patronymic : editPatronymic
        };

        await this.props.libraryService.editAuthor(author);
        this.props.onRequest();
        this.getAllAuthors();
    };

    onDeleteAuthor = async event => {
        event.preventDefault();

        if(this.props.selectedItem != null) {
            await this.props.libraryService.deleteAuthor(this.props.selectedItem);
            this.props.onRequest();
            this.getAllAuthors();
        }
    };

    selectedRow = (id, surname, name, patronymic) => {
        const author = {
            editSurname : surname,
            editName : name,
            editPatronymic : patronymic
        };
        const item = {
            id, author
        };
        this.props.onItemChange(item);
    };

    render() {

        if(this.props.loading) {
            return <Spinner/>
        }

        const rows = this.props.authors.map(({id, surname, name, patronymic}) => {
            return (
                <tr key={id}>
                    <td><input type="radio" name="object_id" value={id} onClick={() => {this.selectedRow(id, surname, name, patronymic)}}/></td>
                    <td>{surname}</td>
                    <td>{name}</td>
                    <td>{patronymic}</td>
                </tr>
            )
        });

        const {addSurname, addName, addPatronymic} = this.props.addAuthor;
        const {editSurname, editName, editPatronymic} = this.props.editAuthor;

        return (

            <div>
                <Header/>

                <main role="main">

                    <div className="container authors-page">
                        <h1>Authors</h1>

                        <div className="genres-tools">
                            <button className="btn btn-outline-danger float-right my-1 item-control" onClick={this.onDeleteAuthor}>
                                <i className="fa fa-trash-o"/>
                            </button>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-author-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-author-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-author-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onAddAuthor}>
                                    <div className="authors-form">
                                        <input type="text" placeholder="Write surname here..." className="form-control mb-2 form-label" onChange={this.changeAddSurname} value={addSurname}/>
                                        <input type="text" placeholder="Write name here..." className="form-control mb-2 form-label" onChange={this.changeAddName} value={addName}/>
                                        <input type="text" placeholder="Write patronymic here..." className="form-control form-label" onChange={this.changeAddPatronymic} value={addPatronymic}/>
                                        <button type="submit" className="btn btn-info form-button mt-0 ml-2">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        <div className="collapse" id="edit-author-block">
                            <div className="card card-body">
                                <form onSubmit={this.onEditAuthor}>
                                    <input type="text" placeholder="Write surname here..." className="form-control mb-2 form-label" onChange={this.changeEditSurname} value={editSurname}/>
                                    <input type="text" placeholder="Write name here..." className="form-control mb-2 form-label" onChange={this.changeEditName} value={editName}/>
                                    <input type="text" placeholder="Write patronymic here..." className="form-control form-label" onChange={this.changeEditPatronymic} value={editPatronymic}/>
                                    <button type="submit" className="btn btn-info form-button">Update</button>
                                </form>
                            </div>
                        </div>

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Surname</th>
                                <th>Name</th>
                                <th>Patronymic</th>
                            </tr>
                            </thead>
                            <tbody>
                            {rows}
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }
}

const mapStateToProps = ({authorsList}) => {
    return {
        authors : authorsList.authors,
        loading : authorsList.loading,
        error : authorsList.error,
        addAuthor : authorsList.addAuthor,
        editAuthor : authorsList.editAuthor,
        selectedItem : authorsList.selectedItem
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        onRequest : () => {dispatch(authorsRequested())},
        onLoaded : (newAuthors) => {dispatch(authorsLoaded(newAuthors))},
        changeAddSurname : (value) => {dispatch(authorUpdateAddSurnameField(value))},
        changeAddName : (value) => {dispatch(authorUpdateAddNameField(value))},
        changeAddPatronymic : (value) => {dispatch(authorUpdateAddPatronymicField(value))},
        changeEditSurname : (value) => {dispatch(authorUpdateEditSurnameField(value))},
        changeEditName : (value) => {dispatch(authorUpdateEditNameField(value))},
        changeEditPatronymic : (value) => {dispatch(authorUpdateEditPatronymicField(value))},
        onItemChange : (item) => {dispatch(authorUpdateSelectedItem(item))}
    }
};

export default compose(
    withLibraryService(),
    connect(mapStateToProps, mapDispatchToProps)
    )(AuthorsPage);
