import React, {Component} from "react";
import Header from "../header";
import Spinner from "../spinner";
import axios from "axios";

class BooksPage extends Component {

    state = {
        books : [],
        loading : true,
        error : false,
        authors : [],
        genres : [],
        publishers : [],
        addBook : {
            addTitle : "",
            addAuthor : null,
            addGenre : null,
            addYear : "",
            addPublisher : null,
            addAmountOfPages : ""
        },
        editBook : {
            editTitle : "",
            editAuthor : null,
            editGenre : null,
            editYear : "",
            editPublisher : null,
            editAmountOfPages : ""
        },
        selectedItem : null
    };

    componentDidMount() {
        this.getAllAuthors();
        this.getAllGenres();
        this.getAllPublishers();
        this.getAllBooks();
    }

    getAllAuthors = async () => {
        await axios.get("http://localhost:8080/authors")
            .then(response => {
                if(response.data != null){
                    this.setState({
                        ...this.state,
                        authors : response.data
                    });
                }
            })
    };

    getAllGenres = async () => {
        await axios.get("http://localhost:8080/genres")
            .then(response => {
                if(response.data != null){
                    this.setState({
                        ...this.state,
                        genres : response.data
                    });
                    console.log(response.data);
                }
            })
    };

    getAllPublishers = async () => {
        await axios.get("http://localhost:8080/publishers")
            .then(response => {
                if(response.data != null) {
                    this.setState({
                        ...this.state,
                        publishers : response.data
                    });
                    console.log(response.data);
                }
            })
    };

    getAllBooks = async () => {
        await axios.get("http://localhost:8080/books")
            .then(response => {
                if(response.data != null) {
                    this.setState({
                        ...this.state,
                        books : response.data,
                        loading : false
                    });
                    console.log(response.data);
                }
            })
    };

    onAddTitle = event => {
        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addTitle : event.target.value,
            }
        });
    };

    onAddAuthor = event => {
        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addAuthor : event.target.value
            }
        });
    };

    onAddGenre = event => {
        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addGenre : event.target.value
            }
        });
    };

    onAddPublisher= event => {
        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addPublisher : event.target.value
            }
        });
    };

    onAddYear= event => {
        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addYear : event.target.value
            }
        });
    };

    onAddAmountOfPages = event => {
        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addAmountOfPages : event.target.value
            }
        });
    };

    onAddBook = async () => {

        const book = {
            title : this.state.addBook.addTitle,
            author_id : this.state.addBook.addAuthor,
            genre_id : this.state.addBook.addGenre,
            year : this.state.addBook.addYear,
            publisher_id : this.state.addBook.addPublisher,
            amount_pages : this.state.addBook.addAmountOfPages
        };

        await axios.post("http://localhost:8080/book-add", book)
            .then(response => {
                if(response.data != null){
                    this.setState({
                        ...this.state,
                        addBook : {
                            addTitle : "",
                            addAuthor : null,
                            addGenre : null,
                            addYear : "",
                            addPublisher : null,
                            addAmountOfPages : ""
                        }
                    })
                }
            });
        
        this.getAllBooks();
    };

    render () {

        if(this.state.loading){
            return <Spinner/>
        }

        const authors = this.state.authors.map(({id, surname, name, patronymic})=> {
            return (
                <option key={id} value={id}>{surname} {name} {patronymic}</option>
            )
        });

        const genres = this.state.genres.map(({id, name}) => {
            return (
                <option key={id} value={id}>{name}</option>
            )
        });

        const publishers = this.state.publishers.map(({id, publisher}) => {
            return (
                <option key={id} value={id}>{publisher}</option>
            )
        });

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Books</h1>

                        <div className="genres-tools">
                            <button className="btn btn-outline-danger float-right my-1 item-control">
                                <i className="fa fa-trash-o"/>
                            </button>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-book-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-book-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-book-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={onAddBook}>
                                    <div className="genre-form">
                                        <input type="text" placeholder="Write title of new book here..." className="form-control mb-2" onChange={this.onAddTitle} value={this.state.addBook.addTitle}/>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="author-list" onChange={this.onAddAuthor}>
                                                {authors}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="author-list" onChange={this.onAddGenre}>
                                                {genres}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="author-list" onChange={this.onAddPublisher}>
                                                {publishers}
                                            </select>
                                        </div>
                                        <input type="text" placeholder="Write year of new book here..." className="form-control mb-2" onChange={this.onAddYear} value={this.state.addBook.addYear}/>
                                        <input type="text" placeholder="Write amount of pages of new book here..." className="form-control mb-2" onChange={this.onAddAmountOfPages} value={this.state.addBook.addAmountOfPages}/>
                                        <button type="submit" className="btn btn-info auth-button">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        {/*<div className="collapse" id="edit-author-block">*/}
                        {/*    <div className="card card-body">*/}
                        {/*        <form onSubmit={this.onEditAuthor}>*/}
                        {/*            <input type="text" placeholder="Write surname here..." className="form-control mb-2" onChange={this.onChangeEditSurname} value={this.state.editAuthor.editSurname}/>*/}
                        {/*            <input type="text" placeholder="Write name here..." className="form-control mb-2" onChange={this.onChangeEditName} value={this.state.editAuthor.editName}/>*/}
                        {/*            <input type="text" placeholder="Write patronymic here..." className="form-control" onChange={this.onChangeEditPatronymic} value={this.state.editAuthor.editPatronymic}/>*/}
                        {/*            <button type="submit" className="btn btn-info auth-button">Update</button>*/}
                        {/*        </form>*/}
                        {/*    </div>*/}
                        {/*</div>*/}

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Title</th>
                                <th>Author</th>
                                <th>Genre</th>
                                <th>Year</th>
                                <th>Publisher</th>
                                <th>Amount of pages</th>
                            </tr>
                            </thead>
                            <tbody>
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }



}

export default BooksPage;
