const updateHomePage = (state ,action) => {

    const initialState = {
        libraryId : "all",
        sortField : 'title'
    };

    if(state === undefined) {
        return initialState;
    }

    switch(action.type) {
        case 'CLEAR_HOMEPAGE':
            return {
                ...initialState,
                libraryId : state.homePage.initLibraryId,
            };
        case 'FETCH_LIBRARIES_SUCCESS':
            return {
                ...state.homePage,
                libraryId: action.payload[0].id
            };
        case 'UPDATE_HOMEPAGE_LIBRARY_ID' :
            return {
                ...state.homePage,
                libraryId: action.payload
            };
        case 'UPDATE_HOMEPAGE_SORT_FIELD' :
            return {
                ...state.homePage,
                sortField: action.payload
            };
        default :
            return state.homePage;
    }

};

export default updateHomePage;
