import React, {Component} from "react";
import "./bookfund-page.css";
import Spinner from "../spinner";
import Header from "../header";
import axios from "axios";

class BookFundPage extends Component {

    state = {
        book_fund : [],
        books : [],
        libraries : [],
        loading : true,
        error : false,
        addBookFund : {
            addBook : {},
            addLibrary : {},
            addAmount : ""
        },
        editBookFund : {
            editBook : {},
            editLibrary : {},
            editAmount : ""
        },
        selectedItem : null
    };

    componentDidMount() {
        this.getAllBooks();
        this.getAllLibraries();
        this.getBookFund();
    }

    getAllBooks = async () => {
        await axios.get("http://localhost:8080/books")
            .then(response => {
                if(response.data != null){
                    this.setState({
                        ...this.state,
                        books : response.data,
                        addBookFund : {
                            ...this.state.addBookFund,
                            addBook: response.data[0]
                        }
                    })
                }
            })
    };

    getAllLibraries = async () => {
        await axios.get("http://localhost:8080/libraries")
            .then(response => {
                if(response.data != null) {
                    this.setState({
                        ...this.state,
                        libraries : response.data,
                        addBookFund : {
                            ...this.state.addBookFund,
                            addLibrary : response.data[0]
                        }
                    })
                }
            })
    };

    getBookFund = async () => {
        await axios.get("http://localhost:8080/bookfund")
            .then(response => {
                if(response.data != null) {
                    this.setState({
                        ...this.state,
                        book_fund : response.data,
                        loading : false
                    })
                }
            })
    };

    findBook = (id) => {
        return this.state.books.find((el) => el.id === +id);
    };

    findLibrary = (id) => {
        return this.state.libraries.find((el) => el.id === +id);
    };

    onAddBook= event => {
        console.log(this.findBook(event.target.value));
        this.setState({
            ...this.state,
            addBookFund : {
                ...this.state.addBookFund,
                addBook : this.findBook(event.target.value)
            }
        });
    };

    onAddLibrary = event => {
        this.setState({
            ...this.state,
            addBookFund : {
                ...this.state.addBookFund,
                addLibrary : this.findLibrary(event.target.value)
            }
        });
    };

    onAddAmount = event => {
        this.setState({
            ...this.state,
            addBookFund : {
                ...this.state.addBookFund,
                addAmount : event.target.value
            }
        })
    };

    onAddBookFund = async event => {
        event.preventDefault();

        const bookFund = {
            book : this.state.addBookFund.addBook,
            library : this.state.addBookFund.addLibrary,
            amount : +this.state.addBookFund.addAmount
        };

        await axios.post("http://localhost:8080/bookfund-add", bookFund)
            .then(response => {
                if(response.data != null){
                    this.setState({
                        ...this.state,
                        addBookFund : {
                            addBook : this.state.books[0],
                            addLibrary : this.state.libraries[0],
                            addAmount: ""
                        }
                    })
                }
            });

        this.getBookFund();
    };

    selectedRow = (id, book, library, amount) => {
        this.setState({
            ...this.state,
            editBookFund : {
                editBook : book,
                editLibrary: library,
                editAmount: amount
            },
            selectedItem : id
        })
    };

    render() {
        if(this.state.loading){
            return <Spinner/>
        }

        const books = this.state.books.map(({id, title, year} )=> {
            return (
                <option key={id} value={id}>{title} ({year})</option>
            )
        });

        const libraries = this.state.libraries.map(({id, name}) => {
            return (
                <option key={id} value={id}>{name}</option>
            )
        });

        const rows = this.state.book_fund.map((el) => {

            const {id, book, library, amount} = el;
            const {title, author, genre, year, publisher, amount_pages } = book;
            const {name, address, telephone} = library;

            return (
                <tr key={id}>
                    <td><input type="radio" name="object_id" value={id} onClick={() => {this.selectedRow(el)}}/></td>
                    <th>{title}</th>
                    <th>{author.surname} {author.name} {author.patronymic}</th>
                    <th>{genre.name}</th>
                    <th>{year}</th>
                    <th>{publisher.publisher}</th>
                    <th>{amount_pages}</th>
                    <th>{name}</th>
                    <th>{address}</th>
                    <th>{telephone}</th>
                </tr>
            )
        });

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>BookFund</h1>

                        <div className="genres-tools">
                            <button className="btn btn-outline-danger float-right my-1 item-control">
                                <i className="fa fa-trash-o"/>
                            </button>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-book-fund-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-book-fund-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-book-fund-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onAddBookFund}>
                                    <div className="genre-form">
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="add-author-list" onChange={this.onAddBook} value={this.state.addBookFund.addBook}>
                                                {books}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="add-genre-list" onChange={this.onAddLibrary} value={this.state.addBookFund.addLibrary}>
                                                {libraries}
                                            </select>
                                        </div>
                                        <input type="text" placeholder="Write year of new book here..." className="form-control mb-2" onChange={this.onAddAmount} value={this.state.addBookFund.addAmount}/>
                                        <button type="submit" className="btn btn-info auth-button">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        {/*<div className="collapse" id="edit-book-block">*/}
                        {/*    <div className="card card-body mb-2">*/}
                        {/*        <form onSubmit={this.onEditBook}>*/}
                        {/*            <div className="genre-form">*/}
                        {/*                <input type="text" placeholder="Write title of new book here..." className="form-control mb-2" onChange={this.onEditTitle} value={this.state.editBook.editTitle}/>*/}
                        {/*                <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">*/}
                        {/*                    <select id="edit-author-list" onChange={this.onEditAuthor} value={this.state.editBook.editAuthor}>*/}
                        {/*                        {authors}*/}
                        {/*                    </select>*/}
                        {/*                </div>*/}
                        {/*                <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">*/}
                        {/*                    <select id="edit-genre-list" onChange={this.onEditGenre} value={this.state.editBook.editGenre}>*/}
                        {/*                        {genres}*/}
                        {/*                    </select>*/}
