import React, {Component} from 'react';
import './genres-page.css';

import Header from '../header';
import axios from 'axios';

class GenresPages extends Component{

    state = {
        genres : [],
        loading : true,
        error : false
    };

    componentDidMount() {
        this.getAllGenres();
    }

    getAllGenres  = () => {
        axios.get("http://localhost:8080/genres")
            .then(response => {
                this.setState({
                    ...this.state,
                    genres: response.data,
                    loading: false
                })});
        console.log(this.state.genres);
    };

    render () {

        if(this.state.loading) {
            return <p>loading....</p>
        }

        const { genres } = this.state;

        const rows = genres.map(({id, name}) => {
            return (
                <tr>
                    <td>{id}</td>
                    <td>{name}</td>
                </tr>
            )
        });

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Genres</h1>

                        <div className="d-flex">
                            <a className="btn btn-outline-danger float-right my-1 item-control" data-toggle="collapse" href="#edit-genre-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-trash-o"/>
                            </a>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-genre-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-genre-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>

                            <div className="collapse" id="add-genre-block">
                                <div className="input-block">
                                    Anim pariatur cliche reprehenderit, enim eiusmod high life accusamus terry richardson ad
                                    squid. Nihil anim keffiyeh helvetica, craft beer labore wes anderson cred nesciunt
                                    sapiente ea proident.
                                </div>
                            </div>

                            <div className="collapse" id="edit-genre-block">
                                <div className="card card-body">
                                    Anim pariatur cliche reprehenderit, enim eiusmod high life accusamus terry richardson ad
                                    squid. Nihil anim keffiyeh helvetica, craft beer labore wes anderson cred nesciunt
                                    sapiente ea proident.
                                </div>
                            </div>
                        </div>



                        <table className="table">
                            <thead>
                            <tr>
                                <th>Id</th>
                                <th>Name</th>
                            </tr>
                            </thead>
                            <tbody>
                                {rows}
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }

};

export default GenresPages;
