import React, {Component} from 'react';
import './genres-page.css';

import Header from '../header';
import axios from 'axios';

class GenresPages extends Component{

    state = {
        genres : [],
        loading : true,
        error : false
    };

    componentDidMount() {
        this.getAllGenres();
    }

    getAllGenres  = () => {
        axios.get("http://localhost:8080/genres")
            .then(response => response.json())
            .then(data => {
                this.setState({
                    ...this.state,
                    genres: data,
                    loading: false
                })})
            .then(async() => {
                this.setState({
                    teachers:this.assembleTeachers(),
                    isLoading:false })
                //console.log(this.state.tableRows);
            });
    }

    render () {

        if(this.state.loading) {
            return <p>loading....</p>
        }

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Genres</h1>

                        <button type="button" className="btn btn-outline-danger float-right my-1 mr-1">
                            <i className="fa fa-trash-o"/>
                        </button>

                        <button type="button" className="btn btn-outline-warning float-right my-1">
                            <i className="fa fa-pencil"/>
                        </button>

                        <button type="button" className="btn btn-outline-success float-right my-1">
                            <i className="fa fa-plus"/>
                        </button>

                        <table className="table">
                            <thead>
                            <tr>
                                <th>Id</th>
                                <th>Name</th>
                            </tr>
                            </thead>
                            <tbody>
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }

};

export default GenresPages;
