import React, {Component} from 'react';
import './registration.css';

import withLibraryService from "../../hoc";
import axios from 'axios';

class Registration extends Component{

    onSubmit = event => {

        const teacher = {
            login : "nina",
            password : "root"
        };

        axios.post("http://localhost:8080/createTeacher", teacher)
            .then(response => {
                if (response.data != null) {
                    this.setState({"show": true, "method": "post"});
                    setTimeout(() => this.setState({"show": false}), 3000);
                } else {
                    this.setState({"show": false});
                }
            });
        this.setState(this.initialState);

    }

    render () {
        return (
            <div className="auth-screen">
                <div className="auth-block align-middle">
                    <form onSubmit={this.onSubmit} method='post'>
                        <h5>Registration</h5>
                        <label className="label-block">
                            Login
                            <input type="text" name="login" placeholder="Write your future login" className="form-control"/>
                        </label>
                        <br/>
                        <label className="label-block">
                            Password
                            <input type="password" name="password" placeholder="Write your future password" className="form-control"/>
                        </label>
                        <br/>
                        <label className="label-block">
                            Confirm password
                            <input type="password" name="passwordRepeat" placeholder="Repeat your password" className="form-control"/>
                        </label>
                        <br/>
                        <button type="submit" className="btn btn-info auth-button">Registration</button>
                    </form>
                </div>
            </div>
        )
    }

};

export default Registration;
