import React, {Component} from "react";
import Header from "../header";
import Spinner from "../spinner";
import axios from "axios";

class BooksPage extends Component {

    state = {
        books : [],
        loading : true,
        error : false,
        authors : [],
        genres : [],
        publishers : [],
        addBook : {
            addTitle : "",
            addAuthor : {},
            addGenre : {},
            addYear : "",
            addPublisher : {},
            addAmountOfPages : ""
        },
        editBook : {
            editTitle : "",
            editAuthor : {},
            editGenre : {},
            editYear : "",
            editPublisher : {},
            editAmountOfPages : ""
        },
        selectedItem : null
    };

    componentDidMount() {
        this.getAllAuthors();
        this.getAllGenres();
        this.getAllPublishers();
        this.getAllBooks();
    }

    getAllAuthors = async () => {
        await axios.get("http://localhost:8080/authors")
            .then(response => {
                if(response.data != null){
                    this.setState({
                        ...this.state,
                        authors : response.data,
                        addBook : {
                            ...this.state.addBook,
                            addAuthor : response.data[0]
                        }
                    });
                }
            })
    };

    getAllGenres = async () => {
        await axios.get("http://localhost:8080/genres")
            .then(response => {
                if(response.data != null){
                    this.setState({
                        ...this.state,
                        genres : response.data,
                        addBook : {
                            ...this.state.addBook,
                            addGenre : response.data[0]
                        }
                    });
                }
            })
    };

    getAllPublishers = async () => {
        await axios.get("http://localhost:8080/publishers")
            .then(response => {
                if(response.data != null) {
                    this.setState({
                        ...this.state,
                        publishers : response.data,
                        addBook : {
                            ...this.state.addBook,
                            addPublisher : response.data[0]
                        }
                    });
                }
            })
    };

    getAllBooks = async () => {
        await axios.get("http://localhost:8080/books")
            .then(response => {
                if(response.data != null) {
                    this.setState({
                        ...this.state,
                        books : response.data,
                        loading : false
                    });
                }
            })
    };

    findAuthor = (id) => {
        return this.state.authors.find((el) => {el.id === id});
    };

    onAddTitle = event => {
        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addTitle : event.target.value,
            }
        });
    };

    onAddAuthor = event => {
        event.preventDefault();
        console.log(event.target.value);

        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addAuthor : this.findAuthor(event.target.value)
            }
        });
    };

    onAddGenre = event => {
        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addGenre : event.target.value
            }
        });
    };

    onAddPublisher= event => {
        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addPublisher : event.target.value
            }
        });
    };

    onAddYear= event => {
        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addYear : event.target.value
            }
        });
    };

    onAddAmountOfPages = event => {
        this.setState({
            ...this.state,
            addBook : {
                ...this.state.addBook,
                addAmountOfPages : event.target.value
            }
        });
    };

    onEditTitle = event => {
        this.setState({
            ...this.state,
            editBook : {
                ...this.state.editBook,
                editTitle : event.target.value,
            }
        });
    };

    onEditAuthor = event => {
        this.setState({
            ...this.state,
            editBook : {
                ...this.state.editBook,
                editAuthor : event.target.value
            }
        });
    };

    onEditGenre = event => {
        this.setState({
            ...this.state,
            editBook : {
                ...this.state.editBook,
                editGenre : event.target.value
            }
        });
    };

    onEditPublisher= event => {
        this.setState({
            ...this.state,
            editBook : {
                ...this.state.editBook,
                editPublisher : event.target.value
            }
        });
    };

    onEditYear= event => {
        this.setState({
            ...this.state,
            editBook : {
                ...this.state.editBook,
                editYear : event.target.value
            }
        });
    };

    onEditAmountOfPages = event => {
        this.setState({
            ...this.state,
            editBook : {
                ...this.state.editBook,
                editAmountOfPages : event.target.value
            }
        });
    };

    onAddBook = async event => {
        event.preventDefault();

        const book = {
            title : this.state.addBook.addTitle,
            author : this.state.addBook.addAuthor,
            genre : this.state.addBook.addGenre,
            year : +this.state.addBook.addYear,
            publisher : this.state.addBook.addPublisher,
            amount_pages : +this.state.addBook.addAmountOfPages
        };

        console.log(book);

        await axios.post("http://localhost:8080/book-add", book)
            .then(response => {
                if(response.data != null){
                    this.setState({
                        ...this.state,
                        addBook : {
                            addTitle : "",
                            addAuthor : this.state.authors[0],
                            addGenre : this.state.genres[0],
                            addYear : "",
                            addPublisher : this.state.publishers[0],
                            addAmountOfPages : ""
                        }
                    })
                }
            });

        this.getAllBooks();
    };

    onEditBook = async event => {
        event.preventDefault();

        const { editTitle, editAuthor, editGenre, editYear, editPublisher, editAmountOfPages} = this.state.editBook;

        const book = {
            id : this.state.selectedItem,
            title : editTitle,
            author : editAuthor,
            genre : editGenre,
            year : +editYear,
            publisher : editPublisher,
            amount_pages : +editAmountOfPages
        };

        console.log(book);

        await axios.put("http://localhost:8080/book-edit", book)
            .then(response => {
                if(response.data != null){
                }
            });

        this.getAllBooks();
    };

    onDeleteBook = async event => {
        event.preventDefault();

        if(this.state.selectedItem != null) {
            await axios.delete(`http://localhost:8080/book-delete/${this.state.selectedItem}`)
                .then(response => {
                    if(response.data != null) {
                        this.setState({
                            ...this.state,
                            editBook : {
                                editTitle : "",
                                editAuthor : {},
                                editGenre : {},
                                editYear : "",
                                editPublisher : {},
                                editAmountOfPages : ""
                            },
                            selectedItem : null
                        })
                    }
                });

            this.getAllBooks();
        }
    };

    selectedRow = (book) => {

        const {id, title, author, genre, year, publisher, amount_pages} = book;
        this.setState({
            ...this.state,
            editBook : {
                editTitle : title,
                editAuthor : author,
                editGenre : genre,
                editYear : year,
                editPublisher : publisher,
                editAmountOfPages : amount_pages
            },
            selectedItem : id
        })
    };

    render () {

        if(this.state.loading){
            return <Spinner/>
        }

        const authors = this.state.authors.map(({id, surname, name, patronymic} )=> {
            return (
                <option key={id} value={id}>{surname} {name} {patronymic}</option>
            )
        });

        const genres = this.state.genres.map((el) => {
            return (
                <option key={el.id} value={el}>{el.name}</option>
            )
        });

        const publishers = this.state.publishers.map((el) => {
            return (
                <option key={el.id} value={el}>{el.publisher}</option>
            )
        });

        const rows = this.state.books.map((el) => {

            const {id, title, author, genre, year, publisher, amount_pages} = el;

            return (
                <tr key={id}>
                    <td><input type="radio" name="object_id" value={id} onClick={() => {this.selectedRow(el)}}/></td>
                    <td>{title}</td>
                    <td>{author.surname} {author.name} {author.patronymic}</td>
                    <td>{genre.name}</td>
                    <td>{year}</td>
                    <td>{publisher.publisher}</td>
                    <td>{amount_pages}</td>
                </tr>
            )
        });

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Books</h1>

                        <div className="genres-tools">
                            <button className="btn btn-outline-danger float-right my-1 item-control" onClick={this.onDeleteBook}>
                                <i className="fa fa-trash-o"/>
                            </button>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-book-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-book-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-book-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onAddBook}>
                                    <div className="genre-form">
                                        <input type="text" placeholder="Write title of new book here..." className="form-control mb-2" onChange={this.onAddTitle} value={this.state.addBook.addTitle}/>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="add-author-list" onChange={this.onAddAuthor} value={this.state.addBook.addAuthor}>
                                                {authors}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="add-genre-list" onChange={this.onAddGenre} value={this.state.addBook.addGenre}>
                                                {genres}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="add-publisher-list" onChange={this.onAddPublisher} value={this.state.addBook.addPublisher}>
                                                {publishers}
                                            </select>
                                        </div>
                                        <input type="text" placeholder="Write year of new book here..." className="form-control mb-2" onChange={this.onAddYear} value={this.state.addBook.addYear}/>
                                        <input type="text" placeholder="Write amount of pages of new book here..." className="form-control mb-2" onChange={this.onAddAmountOfPages} value={this.state.addBook.addAmountOfPages}/>
                                        <button type="submit" className="btn btn-info auth-button">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        <div className="collapse" id="edit-book-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onEditBook}>
                                    <div className="genre-form">
                                        <input type="text" placeholder="Write title of new book here..." className="form-control mb-2" onChange={this.onEditTitle} value={this.state.editBook.editTitle}/>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="edit-author-list" onChange={this.onEditAuthor} value={this.state.editBook.editAuthor}>
                                                {authors}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="edit-genre-list" onChange={this.onEditGenre} value={this.state.editBook.editGenre}>
                                                {genres}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="edit-publisher-list" onChange={this.onEditPublisher} value={this.state.editBook.editPublisher}>
                                                {publishers}
                                            </select>
                                        </div>
                                        <input type="text" placeholder="Write year of new book here..." className="form-control mb-2" onChange={this.onEditYear} value={this.state.editBook.editYear}/>
                                        <input type="text" placeholder="Write amount of pages of new book here..." className="form-control mb-2" onChange={this.onEditAmountOfPages} value={this.state.editBook.editAmountOfPages}/>
                                        <button type="submit" className="btn btn-info auth-button">Update</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Title</th>
                                <th>Author</th>
                                <th>Genre</th>
                                <th>Year</th>
                                <th>Publisher</th>
                                <th>Amount of pages</th>
                            </tr>
                            </thead>
                            <tbody>
                            {rows}
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }



}

export default BooksPage;
