import React, {Component} from 'react';
import './homepage.css';
import axios from "axios";
import {Link} from 'react-router-dom';

class HomePage extends Component{

    state = {
        book_fund : [],
        loading : true,
        error : false
    };

    componentDidMount() {

    }

    getBookFund = async () => {

        await axios.get("http://localhost:8080/bookfund")
            .then(response => {
                if(response.data != null){
                    this.setState({
                        ...this.state,
                        book_fund : response.data,
                        loading : false
                    })
                }
            })

    };

    render () {

        const rows = this.state.book_fund.map((el) => {

            const {id, book, library, amount} = el;
            const {title, author, genre, year, publisher, amount_pages } = book;
            const {name, address, telephone} = library;

            return (
                <tr key={id}>
                    <td>{title}</td>
                    <td>{author.surname} {author.name} {author.patronymic}</td>
                    <td>{genre.name}</td>
                    <td>{year}</td>
                    <td>{publisher.publisher}</td>
                    <td>{amount_pages}</td>
                    <td>{name}</td>
                    <td>{address}</td>
                    <td>{telephone}</td>
                </tr>
            )
        });

        return (
            <div>

                <div className="home-header">
                    <header className="blog-header py-3">
                        <div
                            className="d-flex flex-column flex-md-row align-items-center p-3 px-md-4 mb-3 bg-white border-bottom shadow-sm">
                            <i className="fa fa-book home-logo large"/>
                            <h5 className="my-0 mr-md-auto font-weight-normal">LibraryService</h5>
                            <nav className="my-2 my-md-0 mr-md-3">
                                <Link to="/authorization" className="p-2 text-dark">Log in</Link>
                            </nav>
                        </div>
                    </header>
                </div>


                <main className="container home-content">

                    <h1 className="home-title">BookFund</h1>

                    <div className="home-tools d-flex">

                        <h5 className="d-inline-block home-library align-baseline">Library</h5>

                        <div className="dropdown home-dropdown d-inline-block home-ldropdown">
                            <select>
                                <option>5</option>
                                <option>10</option>
                                <option>15</option>
                                <option>20</option>
                                <option>25</option>
                            </select>
                        </div>

                        <h5 className="d-inline-block home-sort">Sort by</h5>

                        <div className="dropdown home-dropdown d-inline-block home-sdropdown mr-auto">
                            <select>
                                <option>5</option>
                                <option>10</option>
                                <option>15</option>
                                <option>20</option>
                                <option>25</option>
                            </select>
                        </div>

                        <button type="button" className="btn btn-info search-button">Search options</button>

                    </div>

                    <table className="table mt-2">
                        <thead>
                        <tr>
                            <th>Title</th>
                            <th>Author</th>
                            <th>Genre</th>
                            <th>Year</th>
                            <th>Publisher</th>
                            <th>Amount of pages</th>
                            <th>Library</th>
                            <th>Address</th>
                            <th>Telephone</th>
                        </tr>
                        </thead>
                        <tbody>
                        {rows}
                        </tbody>
                    </table>

                </main>

            </div>
        )
    }

}

export default HomePage;
