import axios from "axios";

export default class LibraryService {

    _urlBase = "http://localhost:8080/";

    getAllGenres  = async () => {
        let genres = [];

        await axios.get(`${this._urlBase}/genres`)
            .then(response => {
                if(response.data != null) {
                    genres = response.data;
                }
            });
        
        return genres;
    };

    addGenre = async (genre) => {
        let result = false;

        await axios.post(`${this._urlBase}genre-add`, genre)
            .then(response => {
                if(result.data != null) {
                    result = true;
                }
            });

        return result;
    };

    editGenre = async (genre) => {
        let result = false;

        await axios.put("http://localhost:8080/genre-edit", genre)
            .then(responce => {
                if(responce.data === "") {
                    this.props.onRequest();
                    this.getAllGenres();
                }
            });
    }

};
