import React, {Component} from 'react';
import Header from "../header";
import Spinner from "../spinner";
import axios from "axios";
import {connect} from "react-redux";
import {
    authorsRequested,
    authorsLoaded,
    authorsError,
    authorUpdateAddSurnameField,
    authorUpdateAddNameField,
    authorUpdateAddPatronymicField,
    authorUpdateEditSurnameField,
    authorUpdateEditNameField,
    authorUpdateEditPatronymicField,
    authorUpdateSelectedItem
    } from "../../actions/authorsActions"

class AuthorsPage extends Component {

    componentDidMount() {
        this.props.onRequest();
        this.getAllAuthors();
    }

    getAllAuthors = () => {
        axios.get("http://localhost:8080/authors")
            .then(response => {
                if(response.data != null) {
                    this.props.onLoaded(response.data)
                }
            });
    };

    onChangeAddSurname = event => {
        event.preventDefault();
        this.props.changeAddSurname(event.target.value);
    };

    onChangeAddName = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            addAuthor : {
                ...this.state.addAuthor,
                addName : event.target.value
            }
        })
    };

    onChangeAddPatronymic = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            addAuthor : {
                ...this.state.addAuthor,
                addPatronymic : event.target.value
            }
        })
    };

    onChangeEditSurname = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            editAuthor : {
                ...this.state.editAuthor,
                editSurname : event.target.value
            }
        })
    };

    onChangeEditName = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            editAuthor : {
                ...this.state.editAuthor,
                editName : event.target.value
            }
        })
    };

    onChangeEditPatronymic = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            editAuthor : {
                ...this.state.editAuthor,
                editPatronymic : event.target.value
            }
        })
    };

    onAddAuthor = async event => {
        event.preventDefault();

        const { addAuthor : {addSurname, addName, addPatronymic}} = this.state;

        const author = {
            surname : addSurname,
            name : addName,
            patronymic : addPatronymic
        };

        await axios.post("http://localhost:8080/author-add", author)
            .then(responce => {
                if(responce.data === "") {
                    console.log(responce.data);
                }
            });

        this.getAllAuthors();

    };

    onEditAuthor = async event => {
        event.preventDefault();

        const {editAuthor : {editSurname, editName, editPatronymic}, selectedItem} = this.state;

        const author = {
            id : selectedItem,
            surname : editSurname,
            name : editName,
            patronymic : editPatronymic
        };

        await axios.put("http://localhost:8080/author-edit", author)
            .then((response => {
                if(response.data === "") {
                    console.log(response.data);
                }
            }));

        this.getAllAuthors()

    };

    onDeleteAuthor = async event => {
        event.preventDefault();

        if(this.state.selectedItem != null) {
            await axios.delete(`http://localhost:8080/author-delete/${this.state.selectedItem}`)
                .then(responce => {
                    if (responce.data === "") {
                        console.log(responce.data);
                    }
                });

            this.setState({
                ...this.state,
                selectedItem: null
            });

            this.getAllAuthors();
        }

    };

    selectedRow = (id, surname, name, patronymic) => {
        this.setState({
            ...this.state,
            editAuthor : {
                editSurname: surname,
                editName : name,
                editPatronymic : patronymic
            },
            selectedItem : id
        })
    };

    render() {

        if(this.props.loading) {
            return <Spinner/>
        }

        const rows = this.props.authors.map(({id, surname, name, patronymic}) => {
            return (
                <tr key={id}>
                    <td><input type="radio" name="object_id" value={id} onClick={() => {this.selectedRow(id, surname, name, patronymic)}}/></td>
                    <td>{surname}</td>
                    <td>{name}</td>
                    <td>{patronymic}</td>
                </tr>
            )
        });

        return (

            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Authors</h1>

                        <div className="genres-tools">
                            <button className="btn btn-outline-danger float-right my-1 item-control" onClick={this.onDeleteAuthor}>
                                <i className="fa fa-trash-o"/>
                            </button>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-author-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-author-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-author-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onAddAuthor}>
                                    <div className="genre-form">
                                        <input type="text" placeholder="Write surname here..." className="form-control mb-2" onChange={this.onChangeAddSurname} value={this.state.addAuthor.addSurname}/>
                                        <input type="text" placeholder="Write name here..." className="form-control mb-2" onChange={this.onChangeAddName} value={this.state.addAuthor.addName}/>
                                        <input type="text" placeholder="Write patronymic here..." className="form-control" onChange={this.onChangeAddPatronymic} value={this.state.addAuthor.addPatronymic}/>
                                        <button type="submit" className="btn btn-info auth-button">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        <div className="collapse" id="edit-author-block">
                            <div className="card card-body">
                                <form onSubmit={this.onEditAuthor}>
                                    <input type="text" placeholder="Write surname here..." className="form-control mb-2" onChange={this.onChangeEditSurname} value={this.state.editAuthor.editSurname}/>
                                    <input type="text" placeholder="Write name here..." className="form-control mb-2" onChange={this.onChangeEditName} value={this.state.editAuthor.editName}/>
                                    <input type="text" placeholder="Write patronymic here..." className="form-control" onChange={this.onChangeEditPatronymic} value={this.state.editAuthor.editPatronymic}/>
                                    <button type="submit" className="btn btn-info auth-button">Update</button>
                                </form>
                            </div>
                        </div>

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Surname</th>
                                <th>Name</th>
                                <th>Patronymic</th>
                            </tr>
                            </thead>
                            <tbody>
                            {rows}
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }
}

const mapStateToProps = ({authorsList}) => {
    return {
        authors : authorsList.authors,
        loading : authorsList.loading,
        error : authorsList.error,
        addAuthor : authorsList.addAuthor,
        editAuthor : authorsList.editAuthor,
        selectedItem : authorsList.selectedItem
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        onRequest : () => {dispatch(authorsRequested())},
        onLoaded : (newAuthors) => {dispatch(authorsLoaded(newAuthors))},
        onError : (error) => {dispatch(authorsError(error))},
        changeAddSurname : (value) => {dispatch(authorUpdateAddSurnameField(value))},
        changeAddName : (value) => {dispatch(authorUpdateAddNameField(value))},
        changeAddPatronymic : (value) => {dispatch(authorUpdateAddPatronymicField(value))},
        changeEditSurname : (value) => {dispatch(authorUpdateEditSurnameField(value))},
