import React, {Component} from 'react';
import './genres-page.css';

import Header from '../header';
import Spinner from '../spinner';
import axios from 'axios';
import {compose} from "../../utils";
import {connect} from "react-redux";
import withLibraryService from "../../hoc";
import {genresRequested,
        genresLoaded,
        genresError,
        genreUpdateAddField,
        genreUpdateEditField,
        genreUpdateSelectedItem} from "../../actions/genres-actions";

class GenresPage extends Component{

    componentDidMount() {
        this.props.onRequest();
        this.getAllGenres();
    }

    getAllGenres  = () => {
        const {libraryService} = this.props;
        this.props.onLoaded(libraryService.getAllGenres());
    };

    onAddGenreChange = event => {
        event.preventDefault();
        this.props.onAddFieldChange(event.target.value);
    };

    onEditGenreChange = event => {
        event.preventDefault();
        this.props.onEditFieldChange(event.target.value);
    };

    onAddButtonClick = async event => {
        event.preventDefault();

        const genre = {
            name : this.props.addGenreField
        };

        await axios.post("http://localhost:8080/genre-add", genre)
            .then(responce => {
                this.props.onRequest();
                this.getAllGenres();
            });
    };

    onEditButtonClick = async event => {
        event.preventDefault();

        const genre = {
            id : this.props.selectedItem,
            name : this.props.editGenreField
        };

        await axios.put("http://localhost:8080/genre-edit", genre)
            .then(responce => {
                if(responce.data === "") {
                    this.props.onRequest();
                    this.getAllGenres();
                }
            });
    };

    onDeleteButtonClick = async event => {
        event.preventDefault();

        if (this.props.selectedItem != null) {
            await axios.delete(`http://localhost:8080/genre-delete/${this.props.selectedItem}`)
                .then(responce => {
                    this.props.onRequest();
                    this.getAllGenres();
                });
        }
    };

    selectedRow = (id, text) => {
        const item = {
            id, text
        };
        this.props.onItemChange(item);
    };

    render () {

        if(this.props.loading) {
            return <Spinner/>
        }

        const rows = this.props.genres.map(({id, name}) => {
            return (
                <tr key={id}>
                    <td><input type="radio" name="object_id" value={id} onClick={() => {this.selectedRow(id, name)}}/></td>
                    <td>{name}</td>
                </tr>
            )
        });

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Genres</h1>

                        <div className="genres-tools">
                            <button className="btn btn-outline-danger float-right my-1 item-control" onClick={this.onDeleteButtonClick}>
                                <i className="fa fa-trash-o"/>
                            </button>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-genre-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-genre-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-genre-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onAddButtonClick}>
                                    <div className="genre-form">
                                        <input type="text" placeholder="Write new genre here..." className="form-control" onChange={this.onAddGenreChange} value={this.props.addGenreField}/>
                                        <button type="submit" className="btn btn-info auth-button">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        <div className="collapse" id="edit-genre-block">
                            <div className="card card-body">
                                <form onSubmit={this.onEditButtonClick}>
                                    <input type="text" placeholder="Write new genre here..." className="form-control" onChange={this.onEditGenreChange} value={this.props.editGenreField}/>
                                    <button type="submit" className="btn btn-info auth-button">Update</button>
                                </form>
                            </div>
                        </div>

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Name</th>
                            </tr>
                            </thead>
                            <tbody>
                                {rows}
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }

}

const mapStateToProps = ({genresList}) => {
    return {
        genres : genresList.genres,
        loading : genresList.loading,
        error : genresList.error,
        addGenreField : genresList.addGenreField,
        editGenreField: genresList.editGenreField,
        selectedItem: genresList.selectedItem
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        onRequest : () => {dispatch(genresRequested())},
        onLoaded : (newGenres) => {dispatch(genresLoaded(newGenres))},
        onError : (error) => {dispatch(genresError(error))},
        onAddFieldChange : (value) => {dispatch(genreUpdateAddField(value))},
        onEditFieldChange : (value) => {dispatch(genreUpdateEditField(value))},
        onItemChange : (item) => {dispatch(genreUpdateSelectedItem(item))}
    }

};

export default compose(
    withLibraryService(),
    connect(mapStateToProps, mapDispatchToProps)
    )(GenresPage);
