import React, {Component} from "react";
import ErrorIndicator from "../error-indicator";
import withLibraryService from "../../hoc";
import {
    errorFetch
} from "../../actions/error-actions";

class ErrorBoundary extends Component {

    componentDidCatch(error, errorInfo) {
        this.props.errorFetch(error);
    }

    render() {

        if(this.props.error !== null) {
            return <ErrorIndicator/>;
        }
        return this.props.children;
    }

}

const mapStateToProps = ({error}) => {
    return {
        error : error
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        errorFetch : (error) => {dispatch(errorFetch((error)))}
    }
};

export default withLibraryService(mapStateToProps, mapDispatchToProps)(ErrorBoundary);
