import React, {Component} from "react";
import "./publisher-page.css";
import Header from "../header";
import axios from 'axios';
import Spinner from '../spinner';

class PublishersPage extends Component {

    state = {
        publishers : [],
        loading : true,
        error : false,
        addPublisher : "",
        editPublisher : "",
        selectedItem : null
    };

    componentDidMount() {
        this.getAllPublishers();
    }

    getAllPublishers = () => {

        axios.get("http://localhost:8080/publishers")
            .then(result => {
                if(result.data != null) {
                    this.setState({
                        ...this.state,
                        publishers : result.data,
                        loading : false
                    })
                }
            })
    };

    onChangeAddPublisher = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            addPublisher : event.target.value
        })
    };

    onAddPublisher = async event => {
        event.preventDefault();

        const publisher = {
            publisher : this.state.addPublisher
        };


        console.log('dgh');
        await axios.post("http://localhost:8080/publisher-add", publisher)
                    .then(response => {
                        if(response.data != null) {
                            this.setState({
                                ...this.state,
                                addPublisher : ""
                            })
                        }
                    });

        this.getAllPublishers();

    };

    selectRow = (id, publisher) => {
        this.setState({
            ...this.state,
            editPublisher : publisher,
            selectedItem : id
        })
    };

    render() {

        if(this.state.loading) {
            return <Spinner/>;
        }

        const rows = this.state.publishers.map(({id, publisher}) => {
            return (
                <tr>
                    <td><input type="radio" name="object_id" value={id} onClick={() => {this.selectedRow(id, publisher)}}/></td>
                    <td>{publisher}</td>
                </tr>
            )
        });

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Publishers</h1>

                        <div className="genres-tools">
                            <a className="btn btn-outline-danger float-right my-1 item-control" data-toggle="collapse" role="button">
                                <i className="fa fa-trash-o"/>
                            </a>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-publisher-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-publisher-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-publisher-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onAddPublisher}>
                                    <div className="genre-form">
                                        <input type="text" placeholder="Write new publisher here..." className="form-control" onChange={this.onChangeAddPublisher} value={this.state.addPublisher}/>
                                        <button type="submit" className="btn btn-info auth-button">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        {/*<div className="collapse" id="edit-genre-block">*/}
                        {/*    <div className="card card-body">*/}
                        {/*        <form onSubmit={this.onEditButtonClick}>*/}
                        {/*            <input type="text" placeholder="Write new genre here..." className="form-control" onChange={this.onEditGenreChange} value={this.state.editGenreField}/>*/}
                        {/*            <button type="submit" className="btn btn-info auth-button">Update</button>*/}
                        {/*        </form>*/}
                        {/*    </div>*/}
                        {/*</div>*/}

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Name</th>
                            </tr>
                            </thead>
                            <tbody>{
                                rows
                            }
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        );
    }

}

export default PublishersPage;
