import React, {Component} from 'react';
import './genres-page.css';

import Header from '../header';
import Spinner from '../spinner';
import axios from 'axios';

class GenresPage extends Component{

    state = {
        genres : [],
        loading : true,
        error : false,
        addGenreField : "",
        editGenreField : "",
        selectedGenre : null
    };

    componentDidMount() {
        this.getAllGenres();
    }

    getAllGenres  = () => {
        axios.get("http://localhost:8080/genres")
            .then(response => {
                this.setState({
                    ...this.state,
                    genres: response.data,
                    loading: false
                })});
        console.log(this.state.genres);

    };

    onAddGenreChange = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            addGenreField : event.target.value
        })
    };

    onEditGenreChange = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            editGenreField : event.target.value,
        })
    };

    onAddButtonClick = async event => {
        event.preventDefault();

        const genre = {
            name : this.state.addGenreField
        };

        await axios.post("http://localhost:8080/genre-add", genre)
            .then(responce => {
                if(responce.data === "") {
                    console.log(responce.data);
                }
            });

        this.setState({
            ...this.state,
            addGenreField : ""
        });

        this.getAllGenres();

    };

    onEditButtonClick = async event => {
        event.preventDefault();

        const genre = {
            id : this.state.selectedGenre,
            name : this.state.editGenreField
        };

        await axios.put("http://localhost:8080/genre-edit", genre)
            .then(responce => {
                if(responce.data === "") {
                    console.log(responce.data);
                }
            });

        this.getAllGenres();

    };

    onDeleteButtonClick = async event => {
        event.preventDefault();

        if (this.state.selectedGenre != null) {
            await axios.delete(`http://localhost:8080/genre-delete/${this.state.selectedGenre}`)
                .then(responce => {
                    if (responce.data === "") {
                        console.log(responce.data);
                    }
                });

            this.setState({
                ...this.state,
                selectedGenre: null
            });

            this.getAllGenres();

        }

    };

    selectedRow = (id, text) => {
        console.log(id);
        this.setState({
            ...this.state,
            selectedGenre : id,
            editGenreField : text
        })
    };

    render () {

        if(this.state.loading) {
            return <Spinner/>
        }

        const rows = this.state.genres.map(({id, name}) => {
            return (
                <tr key={id}>
                    <td><input type="radio" name="object_id" value={id} onClick={() => {this.selectedRow(id, name)}}/></td>
                    <td>{name}</td>
                </tr>
            )
        });

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Genres</h1>

                        <div className="genres-tools">
                            <a className="btn btn-outline-danger float-right my-1 item-control" data-toggle="collapse" role="button" onClick={this.onDeleteButtonClick}>
                                <i className="fa fa-trash-o"/>
                            </a>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-genre-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-genre-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-genre-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onAddButtonClick}>
                                    <div className="genre-form">
                                        <input type="text" placeholder="Write new genre here..." className="form-control" onChange={this.onAddGenreChange} value={this.state.addGenreField}/>
                                        <button type="submit" className="btn btn-info auth-button">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        <div className="collapse" id="edit-genre-block">
                            <div className="card card-body">
                                <form onSubmit={this.onEditButtonClick}>
                                    <input type="text" placeholder="Write new genre here..." className="form-control" onChange={this.onEditGenreChange} value={this.state.editGenreField}/>
                                    <button type="submit" className="btn btn-info auth-button">Update</button>
                                </form>
                            </div>
                        </div>

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Name</th>
                            </tr>
                            </thead>
                            <tbody>
                                {rows}
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }

}

export default GenresPage;
