import React, {Component} from 'react';
import Header from "../header";

class AuthorsPage extends Component {

    state = {
        authors : [],
        loading : true,
        error : false,
        addAuthor : {
            addSurname : "",
            addName : "",
            addPatronymic : ""
        },
        editAuthor : {
            editSurname : "",
            editName : "",
            editPatronymic : ""
        },
        selectedItem : null
    };

    onChangeAddSurname = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            addAuthor : {
                ...this.state.addAuthor,
                addSurname : event.target.value
            }
        })
    };

    onChangeAddName = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            addAuthor : {
                ...this.state.addAuthor,
                addName : event.target.value
            }
        })
    };

    onChangeAddPatronymic = event => {
        event.preventDefault();

        this.setState({
            ...this.state,
            addAuthor : {
                ...this.state.addAuthor,
                addPatronymic : event.target.value
            }
        })
    };

    render() {

        return (

            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Authors</h1>

                        <div className="genres-tools">
                            <a className="btn btn-outline-danger float-right my-1 item-control" data-toggle="collapse" role="button">
                                <i className="fa fa-trash-o"/>
                            </a>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-author-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-author-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-author-block">
                            <div className="card card-body mb-2">
                                <form>
                                    <div className="genre-form">
                                        <input type="text" placeholder="Write surname here..." className="form-control mb-2" onChange={this.onChangeAddSurname} value={this.state.addAuthor.addSurname}/>
                                        <input type="text" placeholder="Write name here..." className="form-control mb-2" value={this.state.addAuthor.addName}/>
                                        <input type="text" placeholder="Write patronymic here..." className="form-control" value={this.state.addAuthor.addPatronymic}/>
                                        <button type="submit" className="btn btn-info auth-button">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        {/*<div className="collapse" id="edit-genre-block">*/}
                        {/*    <div className="card card-body">*/}
                        {/*        <form onSubmit={}>*/}
                        {/*            <input type="text" placeholder="Write new genre here..." className="form-control" onChange={} value={}/>*/}
                        {/*            <button type="submit" className="btn btn-info auth-button">Update</button>*/}
                        {/*        </form>*/}
                        {/*    </div>*/}
                        {/*</div>*/}

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Surname</th>
                                <th>Name</th>
                                <th>Patronymic</th>
                            </tr>
                            </thead>
                            <tbody>
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }
}

export default AuthorsPage;
