const genresRequested = () => {
    return {
        type : 'FETCH_GENRES_REQUEST'
    }
};

const genresLoaded = (newGenres) => {
    return {
        type : 'FETCH_GENRES_SUCCESS',
        payload : newGenres
    }
};

const genresError = (error) => {
    return {
        type : 'FETCH_BOOKS_FAILURE',
        payload : error
    }
};

const genreUpdateAddField = (value) => {
    return {
        type : 'UPDATE_GENRE_ADD_FIELD',
        payload : value
    }
};

const genreUpdateEditField = (value) => {
    return {
        type : 'UPDATE_GENRE_EDIT_FIELD',
        payload : value
    }
};

const genreUpdateSelectedItem = (item) => {
    return {
        type : 'UPDATE_GENRE_SELECTED_ITEM',
        payload : item
    }
};
