import axios from "axios";

export default class LibraryService {

    _urlBase = "http://localhost:8080/";

    getAllGenres  = async () => {
        let genres = [];

        await axios.get(`${this._urlBase}genres`)
            .then(response => {
                if(response.data != null) {
                    genres = response.data;
                }
            });
        
        return genres;
    };

    getAllAuthors =  async () => {
        let authors = [];

        await  axios.get(`${this._urlBase}authors`)
            .then(response => {
                if(response.data != null) {
                    authors = response.data;
                }
            });

        return authors;
    };

    getAllPublishers = async() => {
        let publishers = [];

        await axios.get(`${this._urlBase}publishers`)
            .then(response => {
                if(response.data != null) {
                    publishers = response.data;
                }
            });

        return publishers;
    };

    getAllBooks = async() => {
        let books = [];

        await axios.get(`${this._urlBase}books`)
            .then(response => {
                if(response.data != null) {
                    books = response.data;
                }
            });

        return books;
    };



    addGenre = async (genre) => {
        let result = false;

        await axios.post(`${this._urlBase}genre-add`, genre)
            .then(response => {
                if(result.data != null) {
                    result = true;
                }
            });

        return result;
    };

    addAuthor = async (author) => {
        let result = false;

        await axios.post(`${this._urlBase}author-add`, author)
            .then(response => {
                if(response.data === "") {
                    result = true;
                }
            });

        return result;
    };

    addPublisher = async (publisher) => {
        let result = false;

        await axios.post(`${this._urlBase}publisher-add`, publisher)
            .then(response => {
                if(response.data != null) {
                    result = response.data;
                }
            });

        return result;
    };

    addBook = async (book) => {
        let result = false;

        await axios.post(`${this._urlBase}book-add`, book)
            .then(response => {
                if(response.data != null) {
                    result = response.data;
                }
            });

        return result;
    };


    editGenre = async (genre) => {
        let result = false;

        await axios.put(`${this._urlBase}genre-edit`, genre)
            .then(responce => {
                if(responce.data === "") {
                    result = true;
                }
            });

        return result;
    };

    editAuthor = async (author) => {
        let result = false;

        await axios.put(`${this._urlBase}author-edit`, author)
            .then((response => {
                if(response.data === "") {
                    result = true;
                }
            }));

        return result;
    };

    editPublisher = async (publisher) => {
        let result = false;

        await axios.put(`${this._urlBase}publisher-edit`, publisher)
            .then(response => {
                if(response.data != null) {
                    result = true;
                }
            });

        return result;
    };

    editBook = async (book) => {
        let result = false;

        await axios.put(`${this._urlBase}book-edit`, book)
            .then(response => {
                if(response.data != null) {
                    result = true;
                }
            });

        return result;
    };


    deleteGenre = async (id) => {
        let result = false;

        await axios.delete(`http://localhost:8080/genre-delete/${id}`)
            .then(response => {
                if(response.data === "") {
                    response = true;
                }
            });

        return result;
    };

    deleteAuthor = async (id) => {
        let result = false;

        await axios.delete(`${this._urlBase}author-delete/${id}`)
            .then(response => {
                if (response.data === "") {
                    response = true;
                }
            });

        return result;
    };

    deletePublisher = async (id) => {
        let result = false;

        await axios.delete(`${this._urlBase}publisher-delete/${id}`)
            .then(response => {
                if(response.data != null){
                    result = true;
                }
            });

        return result;
    };

    deleteBook = async (id) => {
        let result = false;

        await axios.delete(`${this._urlBase}book-delete/${id}`)
            .then(response => {
                if(response.data != null){
                    result = true;
                }
            });

        return result;
    };

};
