const updateAuthorsList = (state, action) => {

    if(state === undefined) {
        return {
            authors : [],
            loading : true,
            error : false,
            addAuthor : {
                addSurname : "",
                addName : "",
                addPatronymic : ""
            },
            editAuthor : {
                editSurname : "",
                editName : "",
                editPatronymic : ""
            },
            selectedItem : null
        }
    }

    switch (action.type) {
        case 'FETCH_AUTHORS_REQUEST':
            return {
                ...state.authorsList,
                authors : [],
                loading: true,
            };
        case 'FETCH_AUTHORS_SUCCESS':
            return {
                ...state.genresList,
                authors : action.payload,
                loading: false,
                error : false,
                addAuthor : {
                    addSurname : "",
                    addName : "",
                    addPatronymic : ""
                },
                editAuthor : {
                    editSurname : "",
                    editName : "",
                    editPatronymic : ""
                },
                selectedItem : null
            };
        case 'FETCH_AUTHORS_FAILURE':
            return {
                ...state.authorsList,
                authors : [],
                loading: false,
                error: action.payload
            };
        case 'UPDATE_AUTHOR_ADD_SURNAME_FIELD':
            return {
                ...state.authorsList,
                addAuthor: {
                    ...state.authorsList.addAuthor,
                }
            };
        case 'UPDATE_GENRE_EDIT_FIELD':
            return {
                ...state.genresList,
                editGenreField: action.payload
            };
        case 'UPDATE_GENRE_SELECTED_ITEM':
            return {
                ...state.genresList,
                editGenreField: action.payload.text,
                selectedItem: action.payload.id
            };
        default :
            return state.authorsList;
    }
};

export default updateAuthorsList;
