import React, {Component} from "react";
import Header from "../header";
import Spinner from "../spinner";
import axios from "axios";
import {connect} from "react-redux";
import {
    booksRequested,
    booksLoaded,
    booksError,
    booksUpdateAddTitleField,
    booksUpdateAddAuthorField,
    booksUpdateAddGenreField,
    booksUpdateAddYearField,
    booksUpdateAddPublisherField,
    booksUpdateAddPagesField,
    booksUpdateEditTitleField,
    booksUpdateEditAuthorField,
    booksUpdateEditGenreField,
    booksUpdateEditYearField,
    booksUpdateEditPublisherField,
    booksUpdateEditPagesField,
    booksUpdateSelectedItem
} from "../../actions/books-actions";
import {authorsLoaded} from "../../actions/authors-actions";
import {genresLoaded} from "../../actions/genres-actions";
import {publishersLoaded} from "../../actions/publishers-actions";

class BooksPage extends Component {

    componentDidMount() {
        this.props.onRequest();
        this.getAllAuthors();
        this.getAllGenres();
        this.getAllPublishers();
        this.getAllBooks();
    }

    getAllAuthors = async () => {
        await axios.get("http://localhost:8080/authors")
            .then(response => {
                if(response.data != null){
                    this.props.onLoadedAuthors(response.data)
                }
            })
    };

    getAllGenres = async () => {
        await axios.get("http://localhost:8080/genres")
            .then(response => {
                if(response.data != null){
                    this.props.onLoadedGenres(response.data)
                }
            })
    };

    getAllPublishers = async () => {
        await axios.get("http://localhost:8080/publishers")
            .then(response => {
                if(response.data != null) {
                    this.props.onLoadedPublishers(response.data)
                }
            })
    };

    getAllBooks = async () => {
        await axios.get("http://localhost:8080/books")
            .then(response => {
                if(response.data != null) {
                    this.props.onLoaded(response.data)
                }
            })
    };

    findAuthor = (id) => {
        return this.props.authors.find((el) => el.id === +id);
    };

    findGenre = (id) => {
        return this.props.genres.find((el) => el.id === +id);
    };

    findPublisher = (id) => {
        return this.props.publishers.find((el) => el.id === +id);
    };

    changeAddTitle = event => {
        event.preventDefault();
        this.props.changeAddTitle(event.target.value);
    };

    changeAddAuthor = event => {
        event.preventDefault();
        this.props.changeAddAuthor(this.findAuthor(event.target.value));
    };

    changeAddGenre = event => {
        event.preventDefault();
        this.props.changeAddGenre(this.findGenre(event.target.value));
    };

    changeAddPublisher= event => {
        event.preventDefault();
        this.props.changeAddPublisher(this.findPublisher(event.target.value));
    };

    changeAddYear= event => {
        event.preventDefault();
        this.props.changeAddYear(event.target.value);
    };

    changeAddAmountOfPages = event => {
        event.preventDefault();
        this.props.changeAddPages(event.target.value);
    };

    changeEditTitle = event => {
        event.preventDefault();
        this.props.changeEditTitle(event.target.value);
    };

    changeEditAuthor = event => {
        event.preventDefault();
        this.props.changeEditAuthor(this.findAuthor(event.target.value));
    };

    changeEditGenre = event => {
        event.preventDefault();
        this.props.changeEditGenre(this.findGenre(event.target.value));
    };

    changeEditPublisher= event => {
        event.preventDefault();
        this.props.changeEditPublisher(this.findPublisher(event.target.value));
    };

    changeEditYear= event => {
        event.preventDefault();
        this.props.changeEditYear(event.target.value);
    };

    changeEditAmountOfPages = event => {
        event.preventDefault();
        this.props.changeEditPages(event.target.value);
    };

    onAddBook = async event => {
        event.preventDefault();

        const {addTitle, addAuthor, addGenre, addYear, addPublisher, addAmountOfPages} = this.props.addBook;
        const book = {
            title : addTitle,
            author : addAuthor,
            genre : addGenre,
            year : +addYear,
            publisher : addPublisher,
            amount_pages : +addAmountOfPages
        };

        await axios.post("http://localhost:8080/book-add", book)
            .then(response => {
                if(response.data != null){
                    this.props.onRequest();
                    this.getAllBooks();
                }
            });
    };

    onEditBook = async event => {
        event.preventDefault();

        const { editTitle, editAuthor, editGenre, editYear, editPublisher, editAmountOfPages} = this.props.editBook;
        const book = {
            id : this.props.selectedItem,
            title : editTitle,
            author : editAuthor,
            genre : editGenre,
            year : +editYear,
            publisher : editPublisher,
            amount_pages : +editAmountOfPages
        };

        console.log(book);

        await axios.put("http://localhost:8080/book-edit", book)
            .then(response => {
                if(response.data != null){
                    this.props.onRequest();
                    this.getAllBooks();
                }
            });
    };

    onDeleteBook = async event => {
        event.preventDefault();

        if(this.props.selectedItem != null) {
            await axios.delete(`http://localhost:8080/book-delete/${this.props.selectedItem}`)
                .then(response => {
                    if(response.data != null) {
                        this.props.onRequest();
                        this.getAllBooks();
                    }
                });
        }
    };

    selectedRow = (item) => {
        const {id, ...data} = item;
        const book = {
            editTitle : data.title,
            editAuthor : data.author,
            editGenre : data.genre,
            editYear : data.year,
            editPublisher : data.publisher,
            editAmountOfPages : data.amount_pages
        };
        this.props.changeItem({id, book});
    };

    render () {

        if(this.props.loading){
            return <Spinner/>
        }

        const authors = this.props.authors.map(({id, surname, name, patronymic} )=> {
            return (
                <option key={id} value={id}>{surname} {name} {patronymic}</option>
            )
        });

        const genres = this.props.genres.map(({id, name}) => {
            return (
                <option key={id} value={id}>{name}</option>
            )
        });

        const publishers = this.props.publishers.map(({id, publisher}) => {
            return (
                <option key={id} value={id}>{publisher}</option>
            )
        });

        const rows = this.props.books.map((el) => {

            const {id, title, author, genre, year, publisher, amount_pages} = el;

            return (
                <tr key={id}>
                    <td><input type="radio" name="object_id" value={id} onClick={() => {this.selectedRow(el)}}/></td>
                    <td>{title}</td>
                    <td>{author.surname} {author.name} {author.patronymic}</td>
                    <td>{genre.name}</td>
                    <td>{year}</td>
                    <td>{publisher.publisher}</td>
                    <td>{amount_pages}</td>
                </tr>
            )
        });

        const {addTitle, addAuthor, addGenre, addYear, addPublisher, addAmountOfPages} = this.props.addBook;
        const {editTitle, editAuthor, editGenre, editYear, editPublisher, editAmountOfPages} = this.props.editBook;

        // console.log(addAuthor);
        // console.log(addGenre);
        // console.log(editAuthor);

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>Books</h1>

                        <div className="genres-tools">
                            <button className="btn btn-outline-danger float-right my-1 item-control" onClick={this.onDeleteBook}>
                                <i className="fa fa-trash-o"/>
                            </button>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-book-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-book-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-book-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onAddBook}>
                                    <div className="genre-form">
                                        <input type="text" placeholder="Write title of new book here..." className="form-control mb-2" onChange={this.changeAddTitle} value={addTitle}/>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="add-author-list" onChange={this.changeAddAuthor} value={addAuthor.id}>
                                                {authors}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="add-genre-list" onChange={this.changeAddGenre} value={addGenre.id}>
                                                {genres}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="add-publisher-list" onChange={this.changeAddPublisher} value={addPublisher.id}>
                                                {publishers}
                                            </select>
                                        </div>
                                        <input type="text" placeholder="Write year of new book here..." className="form-control mb-2" onChange={this.changeAddYear} value={addYear}/>
                                        <input type="text" placeholder="Write amount of pages of new book here..." className="form-control mb-2" onChange={this.changeAddAmountOfPages} value={addAmountOfPages}/>
                                        <button type="submit" className="btn btn-info auth-button">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        <div className="collapse" id="edit-book-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onEditBook}>
                                    <div className="genre-form">
                                        <input type="text" placeholder="Write title of new book here..." className="form-control mb-2" onChange={this.changeEditTitle} value={editTitle}/>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="edit-author-list" onChange={this.changeEditAuthor} value={editAuthor.id}>
                                                {authors}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="edit-genre-list" onChange={this.changeEditGenre} value={editGenre.id}>
                                                {genres}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="edit-publisher-list" onChange={this.changeEditPublisher} value={editPublisher.id}>
                                                {publishers}
                                            </select>
                                        </div>
                                        <input type="text" placeholder="Write year of new book here..." className="form-control mb-2" onChange={this.changeEditYear} value={editYear}/>
                                        <input type="text" placeholder="Write amount of pages of new book here..." className="form-control mb-2" onChange={this.changeEditAmountOfPages} value={editAmountOfPages}/>
                                        <button type="submit" className="btn btn-info auth-button">Update</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Title</th>
                                <th>Author</th>
                                <th>Genre</th>
                                <th>Year</th>
                                <th>Publisher</th>
                                <th>Amount of pages</th>
                            </tr>
                            </thead>
                            <tbody>
                            {rows}
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }
}

const mapStateToProps = ({authorsList, genresList, publishersList, booksList}) => {
    return {
        books : booksList.books,
        loading : booksList.loading,
        error : booksList.error,
        authors : authorsList.authors,
        genres : genresList.genres,
        publishers : publishersList.publishers,
        addBook : booksList.addBook,
        editBook : booksList.editBook,
        selectedItem : booksList.selectedItem
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        onRequest : () => {dispatch(booksRequested())},
        onLoadedAuthors : (newAuthors) => {dispatch(authorsLoaded(newAuthors))},
        onLoadedGenres : (newGenres) => {dispatch(genresLoaded(newGenres))},
        onLoadedPublishers : (newPublishers) => {dispatch(publishersLoaded(newPublishers))},
        onLoaded : (newBooks) => {dispatch(booksLoaded(newBooks))},
        changeAddTitle : (value) => {dispatch(booksUpdateAddTitleField(value))},
        changeAddAuthor : (value) => {dispatch(booksUpdateAddAuthorField(value))},
        changeAddGenre : (value) => {dispatch(booksUpdateAddGenreField(value))},
        changeAddYear : (value) => {dispatch(booksUpdateAddYearField(value))},
        changeAddPublisher : (value) => {dispatch(booksUpdateAddPublisherField(value))},
        changeAddPages : (value) => {dispatch(booksUpdateAddPagesField(value))},
        changeEditTitle : (value) => {dispatch(booksUpdateEditTitleField(value))},
        changeEditAuthor : (value) => {dispatch(booksUpdateEditAuthorField(value))},
        changeEditGenre : (value) => {dispatch(booksUpdateEditGenreField(value))},
        changeEditYear : (value) => {dispatch(booksUpdateEditYearField(value))},
        changeEditPublisher : (value) => {dispatch(booksUpdateEditPublisherField(value))},
        changeEditPages : (value) => {dispatch(booksUpdateEditPagesField(value))},
        changeItem : (item) => {dispatch(booksUpdateSelectedItem(item))}
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(BooksPage);
