import React, {Component} from 'react';
import './registration-page.css';

import {compose} from "../../utils";
import {connect} from "react-redux";
import {Link} from 'react-router-dom';
import withLibraryService from "../../hoc";
import { withRouter } from "react-router";
import {
    registrationsClearFields,
    registrationUpdateLoginField,
    registrationUpdatePasswordField,
    registrationUpdateRepeatPasswordField
} from "../../actions/registration-actions";

class RegistrationPage extends Component{

    componentDidMount() {
        this.props.clearFields();
    }

    loginChange = event => {
        event.preventDefault();
        this.props.changeLogin(event.target.value);
    }

    passwordChange = event => {
        event.preventDefault();
        this.props.changePassword(event.target.value);
    }

    repeatPasswordChange = event => {
        event.preventDefault();
        this.props.changeRepeatPasword(event.target.value);
    }

    onSubmit = async event => {
        event.preventDefault();

        const admin = {
            login : this.props.loginField,
            password : this.props.passwordField
        };

        let result = await this.props.libraryService.addAdmin(admin);
        console.log(result);

        if(result) {
            this.props.history.push('/authorization');
        } else {
           alert("Get some problems when trying add user");
        }

    }

    render () {

        return (
            <div className="reg-screen">
                <div className="reg-block align-middle">

                    <div className="reg-header ml-4 pl-2 d-flex">
                        <Link className="btn btn-outline-dark float-left my-1 item-control"
                            to="/authorization">
                            <i className="fa fa-arrow-left" aria-hidden="true"/>
                        </Link>
                        <h5 className="align-baseline">Registration</h5>
                    </div>


                    <form onSubmit={this.onSubmit}>
                        <label className="label-block">
                            Login
                            <input type="text" placeholder="Write your future login" className="form-control forom-" onChange={this.loginChange} value={this.props.loginField}/>
                        </label>
                        <br/>
                        <label className="label-block">
                            Password
                            <input type="password" placeholder="Write your future password" className="form-control" onChange={this.passwordChange} value={this.props.passwordField}/>
                        </label>
                        <br/>
                        <label className="label-block">
                            Confirm password
                            <input type="password" placeholder="Repeat your password" className="form-control" onChange={this.repeatPasswordChange} value={this.props.repeatField}/>
                        </label>
                        <br/>
                        <button type="submit" className="btn btn-info auth-button">Registration</button>
                    </form>
                </div>
            </div>
        )
    }

};

const mapStateToPtops = ({registrationBlock}) => {
    return {
        loginField : registrationBlock.loginField,
        passwordField : registrationBlock.passwordField,
        repeatPasswordField : registrationBlock.repeatPasswordField
    }
}

const mapDispatchToPtops = (dispatch) => {
    return {
        clearFields : () => {dispatch(registrationsClearFields())},
        changeLogin : (value) => {dispatch(registrationUpdateLoginField(value))},
        changePassword : (value) => {dispatch(registrationUpdatePasswordField(value))},
        changeRepeatPassword : (value) => {dispatch(registrationUpdateRepeatPasswordField(value))}
    }
}

export default  compose(
    withRouter,
    withLibraryService(),
    connect(mapStateToPtops, mapDispatchToPtops)
)(RegistrationPage);
