import React, {Component} from "react";
import "./bookfund-page.css";
import Spinner from "../spinner";
import Header from "../header";
import axios from "axios";
import {compose} from "../../utils";
import {connect} from "react-redux";
import withLibraryService from "../../hoc";
import {
    bookFundRequested,
    bookFundLoaded,
    bookFundUpdateAddBookField,
    bookFundUpdateAddLibraryField,
    bookFundUpdateAddAmountField,
    bookFundUpdateEditBookField,
    bookFundUpdateEditLibraryField,
    bookFundUpdateEditAmountField,
    booksFundUpdateSelectedItem
} from "../../actions/book-fund-actions";
import {booksLoaded} from "../../actions/books-actions";
import {librariesLoaded} from "../../actions/libraries-actions";

class BookFundPage extends Component {

    componentDidMount() {
        this.props.onRequest();
        this.getAllBooks();
        this.getAllLibraries();
        this.getBookFund();
    }

    getAllBooks = async () => {
        await axios.get("http://localhost:8080/books")
            .then(response => {
                if(response.data != null){
                    this.props.onLoadedBooks(response.data);
                }
            })
    };

    getAllLibraries = async () => {
        await axios.get("http://localhost:8080/libraries")
            .then(response => {
                if(response.data != null) {
                    this.props.onLoadedLibraries(response.data);
                }
            })
    };

    getBookFund = async () => {
        await axios.get("http://localhost:8080/bookfund")
            .then(response => {
                if(response.data != null) {
                    this.props.onLoaded(response.data);
                }
            })
    };

    findBook = (id) => {
        return this.props.books.find((el) => el.id === +id);
    };

    findLibrary = (id) => {
        return this.props.libraries.find((el) => el.id === +id);
    };

    changeAddBook= event => {
        event.preventDefault();
        this.props.changeAddBook(this.findBook(event.target.value));
    };

    changeAddLibrary = event => {
        event.preventDefault();
        this.props.changeAddLibrary(this.findLibrary(event.target.value));
    };

    changeAddAmount = event => {
        event.preventDefault();
        this.props.changeAddAmount(event.target.value);
    };

    changeEditBook= event => {
        event.preventDefault();
        this.props.changeEditBook(this.findBook(event.target.value));
    };

    changeEditLibrary = event => {
        event.preventDefault();
        this.props.changeEditLibrary(this.findLibrary(event.target.value));
    };

    changeEditAmount = event => {
        event.preventDefault();
        this.props.changeEditAmount(event.target.value);
    };

    onAddBookFund = async event => {
        event.preventDefault();

        const bookFund = {
            book : this.state.addBookFund.addBook,
            library : this.state.addBookFund.addLibrary,
            amount : +this.state.addBookFund.addAmount
        };

        await axios.post("http://localhost:8080/bookfund-add", bookFund)
            .then(response => {
                if(response.data != null){
                    this.props.onRequest();
                    this.getBookFund();
                }
            });
    };

    onEditBookFund = async event => {
        event.preventDefault();

        if(this.props.selectedItem != null) {
            const { editBook, editLibrary, editAmount } = this.props.editBookFund;

            const bookFund = {
                id : this.props.selectedItem,
                book : editBook,
                library: editLibrary,
                amount : +editAmount
            };

            await axios.put("http://localhost:8080/bookfund-edit", bookFund)
                .then(response => {
                    if(response.data != null) {
                        this.props.onRequest();
                        this.getBookFund();
                    }
                });
        }
    };

    onDeleteBookFund = async event => {
        event.preventDefault();

        if(this.props.selectedItem != null) {
            await axios.delete(`http://localhost:8080/bookfund-delete/${this.props.selectedItem}`)
                .then(response => {
                    if(response.data != null) {
                        this.props.onRequest();
                        this.getBookFund();
                    }
                });
        }
    };

    selectedRow = ({id, book, library, amount}) => {
        const book_fund = {
            editBook : book,
            editLibrary: library,
            editAmount: amount
        };
        this.props.changeItem({id, book_fund})
    };

    render() {
        if(this.props.loading){
            return <Spinner/>
        }

        const books = this.props.books.map(({id, title, year} )=> {
            return (
                <option key={id} value={id}>{title} ({year})</option>
            )
        });

        const libraries = this.props.libraries.map(({id, name}) => {
            return (
                <option key={id} value={id}>{name}</option>
            )
        });

        const rows = this.props.book_fund.map((el) => {

            const {id, book, library} = el;
            const {title, author, genre, year, publisher, amount_pages } = book;
            const {name, address, telephone} = library;

            return (
                <tr key={id}>
                    <td><input type="radio" name="object_id" value={id} onClick={() => {this.selectedRow(el)}}/></td>
                    <td>{title}</td>
                    <td>{author.surname} {author.name} {author.patronymic}</td>
                    <td>{genre.name}</td>
                    <td>{year}</td>
                    <td>{publisher.publisher}</td>
                    <td>{amount_pages}</td>
                    <td>{name}</td>
                    <td>{address}</td>
                    <td>{telephone}</td>
                </tr>
            )
        });

        const {addBook, addLibrary, addAmount} = this.props.addBookFund;
        const {editBook, editLibrary, editAmount} = this.props.editBookFund;

        return (
            <div>
                <Header/>

                <main role="main">

                    <div className="container genres-page">
                        <h1>BookFund</h1>

                        <div className="genres-tools">
                            <button className="btn btn-outline-danger float-right my-1 item-control" onClick={this.onDeleteBookFund}>
                                <i className="fa fa-trash-o"/>
                            </button>

                            <a className="btn btn-outline-warning float-right my-1 item-control" data-toggle="collapse" href="#edit-book-fund-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-pencil"/>
                            </a>

                            <a className="btn btn-outline-success float-right my-1 item-control" data-toggle="collapse" href="#add-book-fund-block" role="button"
                               aria-expanded="false" aria-controls="collapseExample">
                                <i className="fa fa-plus"/>
                            </a>
                        </div>

                        <br/>
                        <br/>

                        <div className="collapse" id="add-book-fund-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onAddBookFund}>
                                    <div className="genre-form">
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="add-books-list" onChange={this.changeAddBook} value={addBook.id}>
                                                {books}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="add-libraries-list" onChange={this.changeAddLibrary} value={addLibrary.id}>
                                                {libraries}
                                            </select>
                                        </div>
                                        <input type="text" placeholder="Write year of new book here..." className="form-control mb-2" onChange={this.changeAddAmount} value={addAmount}/>
                                        <button type="submit" className="btn btn-info auth-button">Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        <div className="collapse" id="edit-book-fund-block">
                            <div className="card card-body mb-2">
                                <form onSubmit={this.onEditBookFund}>
                                    <div className="genre-form">
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="edit-books-list" onChange={this.changeEditBook} value={editBook.id}>
                                                {books}
                                            </select>
                                        </div>
                                        <div className="dropdown home-dropdown d-inline-block home-ldropdown mb-2">
                                            <select id="edit-libraries-list" onChange={this.changeEditLibrary} value={editLibrary.id}>
                                                {libraries}
                                            </select>
                                        </div>
                                        <input type="text" placeholder="Write year of new book here..." className="form-control mb-2" onChange={this.changeEditAmount} value={editAmount}/>
                                        <button type="submit" className="btn btn-info auth-button">Update</button>
                                    </div>
                                </form>
                            </div>
                        </div>

                        <table className="table">
                            <thead>
                            <tr>
                                <th>#</th>
                                <th>Title</th>
                                <th>Author</th>
                                <th>Genre</th>
                                <th>Year</th>
                                <th>Publisher</th>
                                <th>Amount of pages</th>
                                <th>Library</th>
                                <th>Address</th>
                                <th>Telephone</th>
                            </tr>
                            </thead>
                            <tbody>
                            {rows}
                            </tbody>
                        </table>
                    </div>

                </main>
            </div>
        )
    }
}

const mapStateToProps = ({booksList, librariesList, bookFundList}) => {
    return {
        book_fund : bookFundList.book_fund,
        books : booksList.books,
        libraries : librariesList.libraries,
        loading : bookFundList.loading,
        error : bookFundList.error,
        addBookFund : bookFundList.addBookFund,
        editBookFund : bookFundList.editBookFund,
        selectedItem : bookFundList.selectedItem
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        onRequest : () => {dispatch(bookFundRequested())},
        onLoadedBooks : (newBooks) => {dispatch(booksLoaded(newBooks))},
        onLoadedLibraries : (newLibraries) => {dispatch(librariesLoaded(newLibraries))},
        onLoaded : (newBookFund) => {dispatch(bookFundLoaded(newBookFund))},
        changeAddBook : (value) => {dispatch(bookFundUpdateAddBookField(value))},
        changeAddLibrary : (value) => {dispatch(bookFundUpdateAddLibraryField(value))},
        changeAddAmount : (value) => {dispatch(bookFundUpdateAddAmountField(value))},
        changeEditBook : (value) => {dispatch(bookFundUpdateEditBookField(value))},
        changeEditLibrary : (value) => {dispatch(bookFundUpdateEditLibraryField(value))},
        changeEditAmount : (value) => {dispatch(bookFundUpdateEditAmountField(value))},
        changeItem : (item) => {dispatch(booksFundUpdateSelectedItem(item))}
    }
};

export default compose(
        withLibraryService(),
        connect(mapStateToProps, mapDispatchToProps)
    )(BookFundPage);
