const updateHomePage = (state ,action) => {

    const initialState = {
        libraryId : null,
        initLibraryId : null,
        sortField : "title"
    };

    if(state === undefined) {
        return initialState;
    }
    
    switch(action.type) {
        case 'CLEAR_HOMEPAGE':
            return {
                ...initialState,
                libraryId : state.homePage.initLibraryId,
                initLibraryId : state.homePage.initLibraryId
            };
        case 'FETCH_LIBRARIES_SUCCESS':
            return {
                ...state.homePage,
                libraryId: action.payload[0],
                initLibraryId: action.payload[0]
            };
        case 'UPDATE_HOMEPAGE_LIBRARY_ID' :
            return {
                ...state.homePage,
                libraryId: action.payload
            };
        default :
            return state.homePage;
    }

};

export default updateHomePage;
