import React, {Component} from 'react';
import './registration.css';

import withLibraryService from "../../hoc";
import axios from 'axios';
import {Redirect} from 'react-router-dom';

class Registration extends Component{

    state = {
        login : "",
        password : "",
        success : false
    }

    componentDidMount() {
        this.setState({
            login : "",
            password : ""
        })
    }

    loginChange = event => {
        this.setState({
            ...this.state,
            login : event.target.value
        })
    }

    passwordChange = event => {
        this.setState({
            ...this.state,
            password : event.target.value
        })
    }

    onSubmit = event => {

        const admin = {
            login : this.state.login,
            password : this.state.password
        };

        axios.post("http://localhost:8080/registration", admin)
            .then(response => {
                console.log(response);
                    this.setState({
                        ...this.state,
                        success : true
                    })
            });

    }

    render () {

        if(this.state.success) {
            return <Redirect to='/genres'/>
        }

        return (
            <div className="auth-screen">
                <div className="auth-block align-middle">
                    <form onSubmit={this.onSubmit}>
                        <h5>Registration</h5>
                        <label className="label-block">
                            Login
                            <input type="text" name="login" placeholder="Write your future login" className="form-control" onChange={this.loginChange}/>
                        </label>
                        <br/>
                        <label className="label-block">
                            Password
                            <input type="password" name="password" placeholder="Write your future password" className="form-control" onChange={this.passwordChange}/>
                        </label>
                        <br/>
                        <label className="label-block">
                            Confirm password
                            <input type="password" name="passwordRepeat" placeholder="Repeat your password" className="form-control"/>
                        </label>
                        <br/>
                        <button type="submit" className="btn btn-info auth-button">Registration</button>
                    </form>
                </div>
            </div>
        )
    }

};

export default Registration;
